import React from 'react';
import { Link } from 'react-router-dom';
import './Card.css';

const PromotionCard = ({ promotion, oncClickComments }) => (
  <div className="promotion-card">
    <img
      alt="promotion.title"
      src={promotion.imageUrl}
      className="promotion-card__image"
    />
    <div className="promotion-card__info">
      <h1 className="promotion-card__title">{promotion.title}</h1>
      <span className="promotion-card__price">R$ {promotion.price}</span>
      <footer className="promotion-card__footer">
        {promotion.comments.length > 0 && (
          <div className="promotion-card__comment">
            "{promotion.comments[0].comment}"
          </div>
        )}
        <button className="promotion-card__comments-count" onClick={oncClickComments}>
          {promotion.comments.length}{" "}
          {promotion.comments.length > 1 ? "Coment치rios" : "Coment치rio"}
        </button>
        <a
          href={promotion.url}
          target="_blank"
          rel="noreferrer"
          className="promotion-card__link"
        >
          Ir para o site
        </a>
        <Link to={`/edit/${promotion.id}`} 
              className="promotion-card__edit-buttom"
        >
        Editar
        </Link>
      </footer>
    </div>
  </div>
);

export default PromotionCard;
