import React, { useEffect,use } from 'react';
import UIModal from 'components/UI/Modal/Modal';
import useApi from 'components/utils/useApi';
import PromotionModalCommentsTree from './CommentsTree/CommentsTree';
import './Modal.css'


const PromotionModal = ({ promotionId, onClickClose }) => {
    const [comment, setComment] = useState('')
    const [load, loadInfo] = useApi({
        url: '/comments',
        params: {
            promotionId,
            _expand: 'user'
        }
    });

    const [sendComment, sendCommentInfo] = useApi({
        url: '/comments',
        method: 'POST'
    });

    useEffect(() => {
        load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])
    function onSubmit(ev) {
        ev.preventDefault();

        sendComment({
            data: {
                userId: 1,
                promotionId,
                comment
            }
        })
    }

    return (
      <div>
        <UIModal isOpen onClickClose={onClickClose}>
          <form className="promotion-modal__comments-form" onSubmit={onSubmit}>
            <textarea placeholder="Comentar..."></textarea>
            <button type="submit">Enviar</button>
          </form>
          <h1>Coment√°rios</h1>
          <PromotionModalCommentsTree comments={loadInfo.data} />
        </UIModal>
      </div>
    );
}

export default PromotionModal;
