    const [promotionId, setPromotionId] = useState

    if (error) {
        return <div>Algo de errado aconteceu</div>;
    }

    if (loading || promotions === null) {
        return <div>Carregando...</div>;
    }

    if (promotions.length === 0) {
        return <div>Nenhum resultado encontrado</div>;
    }


