import React, { useState, useEffect, useRef } from 'react';
import useApi from 'components/utils/useApi'
import { Link } from 'react-router-dom'


import PromotionList from 'components/Promotion/List/List'
import UIInfinityScroll from 'components/UI/InfinitScroll/InfinitScroll';

import './Search.css'


const PromotionSearch = () => {
    const mountRef = useRef(null);
    const [search, setSearch] = useState('');
    const [load, loadInfo] = useApi({
      debouncedDelay: 1000,
      url: '/promotions',
      method: 'get',
      params: {
        _embed: "comments",
        _order: "desc",
        _sort: "id",
        _limit: 3,
        _page: 1,
        title_like: search || undefined, 
      },
    });

    useEffect(() => {
       load({
         debounced: mountRef.current,
       });

       if (!mountRef.current) {
         mountRef.current = true;
       }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [search]);



    return (
      <div className="promotion-search">
        <header className="promotion-serch__header">
          <h1>Promo Show</h1>
          <Link to="/create">Nova Promoção</Link>
        </header>
        <input
          type="search"
          className="promotion-search__input"
          placeholder="Buscar"
          value={search}
          onChange={(ev) => setSearch(ev.target.value)}
        />
        <PromotionList 
          promotions={loadInfo.data} 
          loading={loadInfo.loading} 
           error={loadInfo.error} 
          />
        {load}
        <UIInfinityScroll fetchMore={() => console.log('Apareceu na tela')}/>
      </div>
    );
}

export default PromotionSearch;
