import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'
import useApi from 'components/utils/useApi'
import UIButton from 'components/UI/Button/Button';

import PromotionList from 'components/Promotion/List/List'
import UIInfinityScroll from 'components/UI/InfinitScroll/InfinitScroll';

import './Search.css'

const baseParams = {
  _embed: "comments",
  _order: "desc",
  _sort: "id",
  _limit: 3,
};

const PromotionSearch = () => {
    const [page, setPage] = useState(1)
    const mountRef = useRef(null);
    const [search, setSearch] = useState('');
    const [load, loadInfo] = useApi({
      debouncedDelay: 2000,
      url: '/promotions',
      method: 'get',
    });

    useEffect(() => {
      load({
        debounced: mountRef.current,
        params: {
          ...baseParams,
          _page: 1,
          title_like: search || undefined,
        },
      });

       if (!mountRef.current) {
         mountRef.current = true;
       }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [search]);

    function fetchMore() {
      const newPage = page + 1;
      load({
        isFetchMore: true,
        params: {
          ...baseParams,
          _page: newPage,
          title_like: search || undefined,
        },
        updateRequestInfo: (newRequestInfo, prevRequestInfo) => ({
          ...newRequestInfo,
          data: [
            ...prevRequestInfo.data,
            ...newRequestInfo.data,
          ],
        }) 
      });
      setPage(newPage)
    }

    return (
      <div className="promotion-search">
        <header className="promotion-serch__header">
          <h1>Promo Show</h1>
          <UIButton 
            component=""
            to="/create"
            >
            Nova Promoção
          </UIButton>
        </header>
        <input
          type="search"
          className="promotion-search__input"
          placeholder="Buscar"
          value={search}
          onChange={(ev) => setSearch(ev.target.value)}
        />
        <PromotionList
          promotions={loadInfo.data}
          loading={loadInfo.loading}
          error={loadInfo.error}
        />
        {loadInfo.data &&
          !loadInfo.loading &&
          loadInfo.data?.length < loadInfo.total && (
            <UIInfinityScroll fetchMore={fetchMore} />
          )}
      </div>
    );
}

export default PromotionSearch;
